function addSelectListener(){document.querySelector("#committee-select").addEventListener("change",e=>{changeCommittee(e.target.value)})}function changeCommittee(e){document.querySelectorAll(".committee-list").forEach(t=>{t.id==`committee-${e}`?(t.dataset.active=1,t.classList.remove("d-none")):(t.dataset.active=0,t.classList.add("d-none"))}),document.querySelectorAll(".excerpt").forEach(t=>{t.id==`excerpt-${e}`?t.classList.remove("d-none"):t.classList.add("d-none")})}window.onload=function(){addSelectListener()};